DELIMITER $$

/*!50106 CREATE DEFINER=`adminroot`@`%` EVENT `manutenzione_intelligente` ON SCHEDULE EVERY 15 DAY STARTS '2026-02-17 03:05:00' ON COMPLETION PRESERVE ENABLE DO BEGIN

    DECLARE done INT DEFAULT FALSE;
    DECLARE nome_tabella VARCHAR(255);
    DECLARE dimensione_mb DECIMAL(10,2);
    DECLARE spazio_libero_mb DECIMAL(10,2);

    DECLARE cur CURSOR FOR
        SELECT 
            TABLE_NAME,
            (DATA_LENGTH + INDEX_LENGTH)/1024/1024 AS dimensione_mb,
            DATA_FREE/1024/1024 AS spazio_libero_mb
        FROM information_schema.tables
        WHERE 
            TABLE_SCHEMA = DATABASE()
            AND ENGINE = 'InnoDB'
            AND TABLE_TYPE = 'BASE TABLE';

    DECLARE CONTINUE HANDLER FOR NOT FOUND SET done = TRUE;

    OPEN cur;

    loop_tabelle: LOOP

        FETCH cur INTO nome_tabella, dimensione_mb, spazio_libero_mb;

        IF done THEN
            LEAVE loop_tabelle;
        END IF;

        -- Condizioni intelligenti
        IF dimensione_mb > 100
           AND spazio_libero_mb > 50
           AND spazio_libero_mb > (dimensione_mb * 0.20) THEN

            SET @sql = CONCAT('OPTIMIZE TABLE `', nome_tabella, '`');
            PREPARE stmt FROM @sql;
            EXECUTE stmt;
            DEALLOCATE PREPARE stmt;

        END IF;

    END LOOP;

    CLOSE cur;

END */$$
