    READS SQL DATA
    DETERMINISTIC
BEGIN
	SET @iva = 4;
	SET @a = 12.43;
	/*Valore Arrotondato*/
	SET @b = IF(valore<=(FLOOR(valore)+0.5),(FLOOR(valore)+0.5),(FLOOR(valore)+1));
	/*Valore Imponibile*/
	SET @c = ROUND(@b/((iva/100)+1),3);
	/*Valore Ivato*/
	SET @d = ROUND(@c*((iva/100)+1),2);
