/* Function  structure for function  `CostomedioAcquistoPeriodo` */

/*!50003 DROP FUNCTION IF EXISTS `CostomedioAcquistoPeriodo` */;
DELIMITER $$

/*!50003 CREATE DEFINER=`ks_taikun`@`%` FUNCTION `CostomedioAcquistoPeriodo`(idArticolo INT) RETURNS double
    READS SQL DATA
    DETERMINISTIC
BEGIN
    DECLARE RetVal DOUBLE DEFAULT 0;
    DECLARE Trova INT DEFAULT 0;
    DECLARE DataFine DATE;
    DECLARE DataInizio DATE;
    DECLARE DataMinima DATE;

    -- data di fine iniziale (oggi)
    SET DataFine = CURDATE();
    SET DataInizio = DATE_SUB(DataFine, INTERVAL 6 MONTH);

    -- data più remota disponibile per l'articolo
    SELECT MIN(d.DataProtocollo)
    INTO DataMinima
    FROM documenti d
    INNER JOIN documentirighe r ON r.DocumentiId = d.id
    WHERE r.ArticoliId = idArticolo and d.tipodocumentiid in (SELECT id FROM tipodocumenti WHERE Usanelcalcolacosti=1);

    IF DataMinima IS NULL THEN
        RETURN 0;
    END IF;

    Ciclo: REPEAT

        -- verifica se esistono movimenti validi nel periodo
        SELECT d.id
        INTO Trova
        FROM documenti d
        INNER JOIN documentirighe r ON d.id = r.DocumentiId
        WHERE r.importo <> 0
          AND r.qnt <> 0
          AND r.omaggio = 0
          AND d.TipoDocumentiId IN (SELECT id FROM tipodocumenti WHERE Usanelcalcolacosti=1)
          AND r.ArticoliId = idArticolo
          AND d.DataProtocollo BETWEEN DataInizio AND DataFine
        LIMIT 1;

        IF IFNULL(Trova,0) > 0 THEN
            LEAVE Ciclo;
        END IF;

        -- arretra di 6 mesi
        SET DataFine = DataInizio;
        SET DataInizio = DATE_SUB(DataInizio, INTERVAL 6 MONTH);

    UNTIL DataFine < DataMinima
    END REPEAT Ciclo;

    -- se non ha trovato nulla
    IF IFNULL(Trova,0) = 0 THEN
        RETURN 0;
    END IF;

    -- calcolo costo medio
    SELECT SUM(r.Importo) / SUM(r.qnt)
    INTO RetVal
    FROM documentirighe r
    INNER JOIN documenti d ON r.DocumentiId = d.id
    WHERE r.importo <> 0
      AND r.qnt <> 0
      AND r.omaggio = 0
      AND d.TipoDocumentiId IN (SELECT id FROM tipodocumenti WHERE Usanelcalcolacosti=1)
      AND r.ArticoliId = idArticolo
      AND d.DataProtocollo BETWEEN DataInizio AND DataFine;

    RETURN IFNULL(RetVal,0);
END */$$
DELIMITER ;

