    READS SQL DATA
    DETERMINISTIC
BEGIN
	declare pVendita double(15,3);
	declare sc1 double(15,3);
	DECLARE sc2 DOUBLE(15,3);
	DECLARE pIva int;
	Declare Prz double(15,3);
	DECLARE PrzIva DOUBLE(15,3);    
	declare pRichiesto double(15,3);
