/* Procedure structure for procedure `AggiornaCostiArticoli` */

/*!50003 DROP PROCEDURE IF EXISTS  `AggiornaCostiArticoli` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`ks_taikun`@`%` PROCEDURE `AggiornaCostiArticoli`()
BEGIN

    /* =========================================================
       STEP 1 — Ricalcolo COSTO MEDIO
       (solo articoli movimentati di recente)
       ========================================================= */

    REPLACE INTO articoli_costi (
        ArticoliId,
        CostoMedio,
        UltimoCosto,
        CostoValorizzazione,
        DataCalcolo
    )
    WITH RECURSIVE Finestre AS (
        -- prima finestra: oggi → 6 mesi fa
        SELECT
            a.ArticoliId,
            CURDATE() AS DataFine,
            DATE_SUB(CURDATE(), INTERVAL 6 MONTH) AS DataInizio
        FROM (
            SELECT DISTINCT r.ArticoliId
            FROM documentirighe r
            JOIN documenti d ON d.id = r.DocumentiId
            WHERE d.DataProtocollo >= DATE_SUB(CURDATE(), INTERVAL 1 DAY)
        ) a

        UNION ALL

        -- finestre precedenti a ritroso
        SELECT
            f.ArticoliId,
            f.DataInizio AS DataFine,
            DATE_SUB(f.DataInizio, INTERVAL 6 MONTH) AS DataInizio
        FROM Finestre f
        WHERE f.DataInizio >
            (
                SELECT MIN(d.DataProtocollo)
                FROM documenti d
                JOIN documentirighe r 
                    ON r.DocumentiId = d.id
                WHERE r.ArticoliId = f.ArticoliId
            )
    ),
    FinestreValide AS (
        SELECT
            f.ArticoliId,
            f.DataInizio,
            f.DataFine,
            ROW_NUMBER() OVER (
                PARTITION BY f.ArticoliId
                ORDER BY f.DataFine DESC
            ) AS rn
        FROM Finestre f
        WHERE EXISTS (
            SELECT 1
            FROM documentirighe r
            JOIN documenti d 
                ON d.id = r.DocumentiId
            JOIN tipodocumenti t 
                ON t.id = d.TipoDocumentiId
               AND t.Usanelcalcolacosti = 1
            WHERE r.ArticoliId = f.ArticoliId
              AND r.importo <> 0
              AND r.qnt <> 0
              AND r.omaggio = 0
              AND d.DataProtocollo BETWEEN f.DataInizio AND f.DataFine
        )
    )
    SELECT
        fv.ArticoliId,
        SUM(r.Importo) / SUM(r.qnt) AS CostoMedio,
        0 AS UltimoCosto,                                  -- placeholder
        SUM(r.Importo) / SUM(r.qnt) AS CostoValorizzazione, -- temporaneo
        CURDATE() AS DataCalcolo
    FROM FinestreValide fv
    JOIN documentirighe r 
        ON r.ArticoliId = fv.ArticoliId
    JOIN documenti d 
        ON d.id = r.DocumentiId
    JOIN tipodocumenti t
        ON t.id = d.TipoDocumentiId
       AND t.Usanelcalcolacosti = 1
    WHERE fv.rn = 1
      AND r.importo <> 0
      AND r.qnt <> 0
      AND r.omaggio = 0
      AND d.DataProtocollo BETWEEN fv.DataInizio AND fv.DataFine
    GROUP BY fv.ArticoliId;


    /* =========================================================
       STEP 2 — Aggiornamento ULTIMO COSTO
       ========================================================= */

    UPDATE articoli_costi ac
    JOIN (
        SELECT
            r.ArticoliId,
            r.Importo / r.qnt AS UltimoCosto,
            d.DataProtocollo  AS DataUltimoCosto
        FROM documentirighe r
        JOIN documenti d ON d.id = r.DocumentiId
        JOIN tipodocumenti t
            ON t.id = d.TipoDocumentiId
           AND t.Usanelcalcolacosti = 1
        WHERE r.importo <> 0
          AND r.qnt <> 0
          AND r.omaggio = 0
          AND (r.ArticoliId, d.DataProtocollo) IN (
              SELECT
                  r2.ArticoliId,
                  MAX(d2.DataProtocollo)
              FROM documentirighe r2
              JOIN documenti d2 ON d2.id = r2.DocumentiId
              JOIN tipodocumenti t2
                  ON t2.id = d2.TipoDocumentiId
                 AND t2.Usanelcalcolacosti = 1
              WHERE r2.importo <> 0
                AND r2.qnt <> 0
                AND r2.omaggio = 0
              GROUP BY r2.ArticoliId
          )
    ) x ON x.ArticoliId = ac.ArticoliId
    SET ac.UltimoCosto = x.UltimoCosto,
	ac.DataUltimoCosto = x.DataUltimoCosto;

    /* =========================================================
       STEP 3 — Calcolo COSTO DI VALORIZZAZIONE
       ========================================================= */

    UPDATE articoli_costi
    SET CostoValorizzazione =
        CASE
            WHEN UltimoCosto > 0 THEN UltimoCosto
            ELSE CostoMedio
        END;

END */$$
DELIMITER ;

