/* Procedure structure for procedure `AllineaQtaEvasa` */

/*!50003 DROP PROCEDURE IF EXISTS  `AllineaQtaEvasa` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`ks_taikun`@`%` PROCEDURE `AllineaQtaEvasa`()
BEGIN
		UPDATE documentirighe r
		JOIN documenti t ON t.id = r.documentiid
		LEFT JOIN (
		  SELECT idRigaEvasa AS id_padre, SUM(qnt) AS somma_evasa
		  FROM documentirighe
		  WHERE idRigaEvasa IS NOT NULL
		  GROUP BY idRigaEvasa
		) x ON x.id_padre = r.id
		SET
		  r.qntevasa     = COALESCE(x.somma_evasa, 0),
		  r.qntevadibile = GREATEST(r.qnt - COALESCE(x.somma_evasa, 0), 0)
		WHERE
		  t.datadocumento >= MAKEDATE(YEAR(CURDATE()), 1)
		  AND t.datadocumento <  CURDATE() + INTERVAL 1 DAY
		  AND (
		    r.qntevasa <> COALESCE(x.somma_evasa, 0)
		    OR r.qntevadibile <> GREATEST(r.qnt - COALESCE(x.somma_evasa, 0), 0)
		  );
	END */$$
DELIMITER ;

