BEGIN
		UPDATE articoli_giacenze fakeGiac
		INNER JOIN 
		(
		SELECT 
			documentirighe.articoliid,
			CASE WHEN TipoDocumenti.Segno<>0 THEN SUM(documentirighe.Qnt*tipodocumenti.Segno) END AS Qnt
		FROM 
			documenti
		INNER JOIN documentirighe ON documentirighe.DocumentiId=documenti.id
		INNER JOIN tipodocumenti ON tipodocumenti.id=documenti.TipoDocumentiId
		WHERE articoliid>0 
		GROUP BY documentirighe.ArticoliId 
		HAVING IFNULL(qnt,99999)<>99999 
		) RealGiac ON realgiac.articoliid=fakegiac.ArticoliId 
		SET 
		fakegiac.Giacenza=realgiac.qnt
		WHERE fakegiac.MagazziniId=1;
	END */$$
