/*!50003 CREATE DEFINER=`ks_taikun`@`%` PROCEDURE `AlterAllTablesEngine`()
BEGIN
    DECLARE done INT DEFAULT FALSE;
    DECLARE nome_tabella VARCHAR(255);
    DECLARE cur CURSOR FOR 
        SELECT TABLE_NAME FROM information_schema.tables 
        WHERE table_type = 'BASE TABLE' AND TABLE_SCHEMA = DATABASE();   -- Usa il database corrente
    DECLARE CONTINUE HANDLER FOR NOT FOUND SET done = TRUE;
    OPEN cur;
    
    loop_tabelle: LOOP
        FETCH cur INTO nome_tabella;
        IF done THEN
            LEAVE loop_tabelle;
        END IF;
        SET @sql = CONCAT('ALTER TABLE `', nome_tabella, '` ENGINE = "InnoDB";');
        PREPARE stmt FROM @sql;
        EXECUTE stmt;
        DEALLOCATE PREPARE stmt;
    END LOOP;
    
    CLOSE cur;
END */$$
