/* Procedure structure for procedure `ArticoliInOfferta` */

/*!50003 DROP PROCEDURE IF EXISTS  `ArticoliInOfferta` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`ks_taikun`@`%` PROCEDURE `ArticoliInOfferta`(in pdata date,in pnListino int(11),in pAzienda int(11))
Blocco1:BEGIN
	DECLARE pOffertaId int(11);
	DECLARE pOffertaDettaglioId int(11);
	DECLARE pMarca int(11);
	DECLARE pSettore int(11);
	DECLARE pCategoria int(11);
	DECLARE pTipoloiga int(11);
	DECLARE pGruppo int(11);
	DECLARE pSottoGruppo int(11);
	DECLARE pArticolo int(11);
	DECLARE finito int(11) default 0;
	DECLARE sSQL varchar(255) default '';
	DECLARE InOfferta cursor for
	SELECT
	offerte.id
	, offertedettaglio.id
	, offertedettaglio.MarcheId
	, offertedettaglio.SettoriId
	, offertedettaglio.CategorieId
	, offertedettaglio.TipologieId
	, offertedettaglio.GruppiId
	, offertedettaglio.SottoGruppiId
	, offertedettaglio.ArticoliId
	FROM
	offertedettaglio
	INNER JOIN offerte 
		ON (offertedettaglio.offerteid = offerte.id)
	where (pnlistino between offerte.dalistino and offerte.alistino) and 
	      (pdata BETWEEN offerte.DataInizio and offerte.DataFine) and
	      AziendeId=pAzienda and
	      offerte.abilitato=1;
	DECLARE CONTINUE HANDLER FOR SQLSTATE '02000' SET finito = 1;
	open InOfferta;
	FETCH InOfferta into pOffertaId,pOffertaDettaglioId,pMarca,pSettore,pCategoria,pTipoloiga,pGruppo,pSottoGruppo,pArticolo;
	
	
	
	 
	CREATE TABLE TArticoliinOfferta (ArticoliId int(11),OffertaId int(11),OffertaDettaglioId int(11));
	Ciclo: REPEAT
		set sSQL='';
		if not pMarca is null then
			set sSQL = concat('MarcheId=', pMarca , ' and ');
		end if;
		if not pSettore is null then 
			set sSQL = concat(sSQL , 'SettoriId=' , pSettore , ' and ');
		end if;
		if not pCategoria is null then 
			set sSQL = concat(sSQL , 'CategorieId=' , pCategoria , ' and ');
		end if;
		if not pGruppo is null then 
			set sSQL = concat(sSQL , 'GruppiId=' , pGruppo , ' and ');
		end if;
		if not pSottoGruppo is null then 
			set sSQL = concat(sSQL , 'SottoGruppiId=' , pSottoGruppo , ' and ');
		end if;
		if not pArticolo is null then
			set sSQL = concat(sSQL , 'ArticoliId=' , pArticolo , ' and ');
		end if;
		set sSQL=concat(' ',mid(sSQL,1,length(sSQL)-5));
		Blocco2:Begin
			declare no_more_rows int(11) default 0;
			declare articolotrovato int(11);
			DECLARE selezionati CURSOR for
			select id from articoli where sSQL;
			DECLARE CONTINUE HANDLER FOR SQLSTATE '02000' SET no_more_rows = 1;
			open selezionati;
			fetch selezionati into articolotrovato;
			Ciclo2: repeat
				insert into TArticoliinOfferta set
				TArticoliinOfferta.ArticoliId=articolotrovato,
				TArticoliinOfferta.OffertaId=pOffertaId,
				TArticoliinOfferta.OffertaDettaglioId=pOffertaDettaglioId;
				fetch selezionati into articolotrovato;
				until no_more_rows = 1
			end repeat Ciclo2;
			close selezionati;
		end;
		FETCH InOfferta into pOffertaId,pOffertaDettaglioId,pMarca,pSettore,pCategoria,pTipoloiga,pGruppo,pSottoGruppo,pArticolo;
	UNTIL finito=1
	END REPEAT Ciclo;
	close InOfferta;
    END */$$
DELIMITER ;

