/* Procedure structure for procedure `ArticoloInDocumenti` */

/*!50003 DROP PROCEDURE IF EXISTS  `ArticoloInDocumenti` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`ks_taikun`@`%` PROCEDURE `ArticoloInDocumenti`(in parArticoliId int)
BEGIN
	if pararticoliId=0 then 
		begin
			SELECT DISTINCT
				tipodocumenti.Descrizione AS TipoDoc
				,case when u.clienti=1 then documenti.NDocumento else documenti.NProtocollo end as NDocumento
				,CASE WHEN u.clienti=1 THEN documenti.DataDocumento ELSE documenti.DataProtocollo END AS DataDocumento
				,CONCAT(utenti.RagioneSociale,' ',IFNULL(utenti.CognomeNome,'')) AS Utente
				,documentipie.TotaleDocumento
			FROM 
				documenti
				INNER JOIN documentirighe ON documentirighe.DocumentiId=documenti.id
				INNER JOIN documentipie ON documentipie.documentiid=documenti.id
				INNER JOIN tipodocumenti ON tipodocumenti.id=documenti.TipoDocumentiId
				INNER JOIN utenti ON utenti.id=documenti.utentiid
				inner join utentitipo U on utenti.UtentiTipoId=U.id
			
			ORDER BY documenti.datadocumento DESC,documenti.ndocumento,documenti.TipoDocumentiId;
		end;
	else
		BEGIN
			SELECT DISTINCT
				tipodocumenti.Descrizione AS TipoDoc,
				case when TipoDocumenti.Segno<0 then 'Scarico' when tipodocumenti.Segno>0 then 'Carico' else 'No Mov.' end as Segno	
				,CASE WHEN TipoDocumenti.Segno<>0 then (documentirighe.Qnt*tipodocumenti.Segno) else documentirighe.Qnt end as Qnt
				,documentirighe.Qntevasa AS Evasa
				,documentirighe.Qntevadibile AS Evadibile
				,CASE WHEN u.clienti=1 THEN documenti.NDocumento ELSE documenti.NProtocollo END AS NDocumento
				,CASE WHEN u.clienti=1 THEN documenti.DataDocumento ELSE documenti.DataProtocollo END AS DataDocumento
				,CONCAT(utenti.RagioneSociale,' ',IFNULL(utenti.CognomeNome,'')) AS Utente
				,documentirighe.prezzo
				,documentirighe.sc1 
				,documentirighe.sc2
				,documentirighe.sc3
				,documentirighe.iva
			FROM 
				documenti
				INNER JOIN documentirighe ON documentirighe.DocumentiId=documenti.id
				INNER JOIN documentipie ON documentipie.documentiid=documenti.id
				INNER JOIN tipodocumenti ON tipodocumenti.id=documenti.TipoDocumentiId
				INNER JOIN utenti ON utenti.id=documenti.utentiid
				INNER JOIN utentitipo U ON utenti.UtentiTipoId=U.id
			WHERE 
				documentirighe.articoliid=parArticoliId
			ORDER BY documenti.datadocumento DESC,documenti.ndocumento,documenti.TipoDocumentiId;
		END;
	end if;
	
    END */$$
DELIMITER ;

