DELIMITER $$

/*!50003 CREATE DEFINER=`ks_taikun`@`%` PROCEDURE `CastellettoIva`(in pDocumento int)
BEGIN
    select x.iva,x.codice,x.descrizione,sum(x.totimponibile) as totimponibile,sum(x.imposta) as imposta from (
	SELECT '0' as idRiga,iva.`Valore` AS iva,iva.codice,iva.descrizione, SUM(importo) AS TotImponibile, SUM(importo*iva.valore/100) AS Imposta 
	FROM documentirighe
	LEFT JOIN iva ON documentirighe.iva=iva.id 
	WHERE documentirighe.documentiid =pDocumento
	GROUP BY iva 
	UNION 
	SELECT '1' as idRiga, iva.`Valore` AS iva,iva.codice,iva.descrizione, SUM(documentipie.costospedizione+documentipie.costoassicurazione) AS TotImponibile, SUM((documentipie.costospedizione+documentipie.costoassicurazione)*iva.valore/100) AS Imposta 
	FROM documentipie
	LEFT JOIN vettori ON documentipie.VettoriId=vettori.id
	LEFT JOIN iva ON vettori.Iva=iva.id
	WHERE documentipie.documentiid =pDocumento
	GROUP BY iva
	HAVING SUM((documentipie.costospedizione+documentipie.costoassicurazione)*iva.valore/100)>0) as x 
	group by x.iva,x.codice,x.descrizione;
    END */$$
DELIMITER ;

