DELIMITER $$

/*!50003 CREATE DEFINER=`ks_taikun`@`%` PROCEDURE `ControllaUtente`(in pPiva varchar(11), in pCodiceFiscale varchar(16),in pUtenteTipoId int(11),out xVal int(11))
BEGIN
	Declare xTipoUtente varchar(4);
	set xVal=0;
	
	select concat(Clienti,Fornitori,Operatori,Agenti) INTO xTipoUtente
	from utentitipo 
	where id=pUtenteTipoId;
	
	if xTipoUtente='1000' then 
		if ifnull(pPiva,'')<>'' then 
			SELECT u.id into xVal
			FROM utenti U 
			inner join utentitipo T on u.utentitipoid=t.id
			WHERE (u.piva=pPiva) and t.clienti=1 limit 1;
		end if;
		If ifnull(pCodiceFiscale,'')<>'' then 
			SELECT u.id INTO xVal
			FROM utenti U 
			INNER JOIN utentitipo T ON u.utentitipoid=t.id
			WHERE (u.codicefiscale=pCodicefiscale) AND t.clienti=1 LIMIT 1;
		end if;
	end if;
	
	IF xTipoUtente='0100' THEN 
		IF IFNULL(pPiva,'')<>'' THEN 
			SELECT u.id INTO xVal
			FROM utenti U 
			INNER JOIN utentitipo T ON u.utentitipoid=t.id
			WHERE (u.piva=pPiva) AND t.fornitori=1 LIMIT 1;
		END IF;
		IF IFNULL(pCodiceFiscale,'')<>'' THEN 
			SELECT u.id INTO xVal
			FROM utenti U 
			INNER JOIN utentitipo T ON u.utentitipoid=t.id
			WHERE (u.codicefiscale=pCodicefiscale) AND t.fornitori=1 LIMIT 1;
		END IF;	
	END IF;
	
	IF xTipoUtente='0010' THEN 
		IF IFNULL(pPiva,'')<>'' THEN 
			SELECT u.id INTO xVal
			FROM utenti U 
			INNER JOIN utentitipo T ON u.utentitipoid=t.id
			WHERE (u.piva=pPiva) AND t.operatori=1 LIMIT 1;
		END IF;
		IF IFNULL(pCodiceFiscale,'')<>'' THEN 
			SELECT u.id INTO xVal
			FROM utenti U 
			INNER JOIN utentitipo T ON u.utentitipoid=t.id
			WHERE (u.codicefiscale=pCodicefiscale) AND t.operatori=1 LIMIT 1;
		END IF;	
