/* Procedure structure for procedure `ConversioneCollateDatabase` */

/*!50003 DROP PROCEDURE IF EXISTS  `ConversioneCollateDatabase` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`ks_taikun`@`%` PROCEDURE `ConversioneCollateDatabase`()
BEGIN
  DECLARE v_finished INT DEFAULT FALSE;
  DECLARE v_table_name VARCHAR(255);
  DECLARE v_alter_stmt LONGTEXT;
  -- Cursor per elencare le tabelle che hanno colonne da convertire
  DECLARE table_cursor CURSOR FOR
    SELECT DISTINCT T.TABLE_NAME
    FROM INFORMATION_SCHEMA.`TABLES` T
	INNER JOIN INFORMATION_SCHEMA.`COLUMNS` C ON T.table_name=c.Table_name
    WHERE T.TABLE_SCHEMA = DATABASE()
	AND C.TABLE_NAME NOT LIKE 'data_amaz%'
	AND C.TABLE_NAME NOT LIKE 'ks_%'
	AND T.`TABLE_TYPE`='BASE TABLE'
	AND CHARACTER_SET_NAME = 'latin1'
	AND COLLATION_NAME = 'latin1_swedish_ci'
	AND C.`CHARACTER_MAXIMUM_LENGTH`<=1650
	AND DATA_TYPE IN ('varchar', 'text')
    GROUP BY T.Table_Name
    ORDER BY T.Table_Name;
  DECLARE CONTINUE HANDLER FOR NOT FOUND SET v_finished = TRUE;
  OPEN table_cursor;
  get_table: LOOP
    FETCH table_cursor INTO v_table_name;
    IF v_finished THEN 
      LEAVE get_table;
    END IF;
    -- Costruisce dinamicamente l'elenco delle colonne da modificare per la tabella corrente
    SELECT GROUP_CONCAT(
             CONCAT('MODIFY `', COLUMN_NAME, '` ', COLUMN_TYPE,
                    ' CHARACTER SET utf8mb4 COLLATE utf8mb4_0900_ai_ci')
             SEPARATOR ', ')
    INTO v_alter_stmt
    FROM INFORMATION_SCHEMA.COLUMNS
    WHERE TABLE_SCHEMA = DATABASE()
      AND TABLE_NAME = v_table_name
      AND CHARACTER_SET_NAME = 'latin1'
      AND COLLATION_NAME = 'latin1_swedish_ci'
      AND DATA_TYPE IN ('varchar', 'text');
    -- Costruisce e esegue l'ALTER TABLE completo
    SET @sql = CONCAT('ALTER TABLE `', v_table_name, '` ', v_alter_stmt, ';');
    PREPARE stmt FROM @sql;
    EXECUTE stmt;
    DEALLOCATE PREPARE stmt;
  END LOOP;
  CLOSE table_cursor;
END */$$
DELIMITER ;

