DELIMITER $$

/*!50003 CREATE DEFINER=`ks_taikun`@`%` PROCEDURE `DettaglioConfigurazione`(in pConf int(11),pListino int(11))
BEGIN
	DECLARE pMarca int(11);
	Declare pSettore int(11);
	DECLARE pCategoria INT(11);
	DECLARE pTipologia INT(11);
	DECLARE pGruppo INT(11);
	DECLARE pSottogruppo INT(11);
	DECLARE pArt INT(11);
	
	select marcheid,Settoriid,Categorieid, TipologieId, GruppiId, Sottogruppiid, ArticoliId
	into pMarca,pSettore,pCategoria,pTipologia,pGruppo,pSottoGruppo,pArt
	from configuratoredettaglio 
	where id=pConf;
	
	set @s=concat('Select * from vArticoliListini where nlistino=',pListino);
	
	if ifnull(pmarca,-1)<>-1 then 
		SET @s = CONCAT(@s,' and marcheid=',pmarca);
	end if;
	
	IF IFNULL(pSettore,-1)<>-1 THEN 
		SET @s = CONCAT(@s,' and Settoriid=',psettore);
	END IF;
	
	IF IFNULL(pCategoria,-1)<>-1 THEN 
		SET @s = CONCAT(@s,' and Categorieid=',pCategoria);
	END IF;
	
	IF IFNULL(pTipologia,-1)<>-1 THEN 
		SET @s = CONCAT(@s,' and Tipologieid=',pTipologia);
	END IF;
	
	IF IFNULL(pGruppo,-1)<>-1 THEN 
		SET @s = CONCAT(@s,' and GruppiId=',pGruppo);
	END IF;
	
	IF IFNULL(pSottoGruppo,-1)<>-1 THEN 
		SET @s = CONCAT(@s,' and SottoGruppiId=',pSottoGruppo);
	END IF;
	 
	PREPARE stmt FROM @s;
	EXECUTE stmt;
	DEALLOCATE PREPARE stmt;
	
    END */$$
DELIMITER ;

