/* Procedure structure for procedure `ElencoDocumentiAnno` */

/*!50003 DROP PROCEDURE IF EXISTS  `ElencoDocumentiAnno` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`ks_taikun`@`%` PROCEDURE `ElencoDocumentiAnno`(IN parTipoDocumento INT(11),IN parAnno INT,IN parListaVuota INT(1))
BEGIN
	SELECT 
		 documenti.id
		,documenti.TipoDocumentiId AS TipoDocId
		,tipodocumenti.Descrizione AS TipoDoc
		,documenti.NDocumento
		,documenti.Bis
		,documenti.DataDocumento
		,trim(concat(utenti.RagioneSociale,' ',ifnull(utenti.CognomeNome,''))) as Utente
		,documenti.NProtocollo
		,documenti.DataProtocollo
		,pagamentitipo.descrizione AS Pagamento
		,case when ifnull(ds.descrizione1,'')<>'' then ds.descrizione1 else documentistati.Descrizione1 end AS Stato
		,documentipie.TotaleDocumento 
		,documenti.Tracking AS Tracking
		,documenti.Pagato AS Pagato
		,documenti.IdTransazione AS Transazione
		,ifnull(b.CodiceAutorizzazione,'') as 'E-Pay'
		,ks_amazon_orders.amazon_order_id as 'N. Amazon'
        FROM 
		documenti
	left JOIN 
		UTENTI ON documenti.UtentiId=utenti.Id
        left JOIN 
		tipodocumenti ON documenti.TipoDocumentiId = tipodocumenti.id
        left JOIN 
		documentipie ON documenti.id = documentipie.DocumentiId
        LEFT JOIN 
		pagamentitipo ON documenti.PagamentiTipoId=pagamentitipo.id
	LEFT JOIN
	        documentistati ON documenti.StatiId=documentistati.id
	Left Join 
		ks_amazon_orders on documenti.id=ks_amazon_orders.document_id
	Left join 
		ks_relation_document_status on ks_amazon_orders.order_status=ks_relation_document_status.order_status
	left join 
		documentistati DS ON ks_relation_document_status.documentistatiid=DS.id
	LEFT JOIN 
		bancasella_ordini_pagati B on documenti.id=b.documentiid
	WHERE 
		documenti.TipoDocumentiId=partipodocumento AND YEAR(documenti.datadocumento)=parAnno AND 1=parlistavuota
	ORDER BY
		documenti.NDocumento DESC
		,documenti.Bis;
		
 END */$$
DELIMITER ;

