/* Procedure structure for procedure `GetSchedeConStato` */

/*!50003 DROP PROCEDURE IF EXISTS  `GetSchedeConStato` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`ks_taikun`@`%` PROCEDURE `GetSchedeConStato`(in parAnno int(11))
BEGIN
  SELECT
    si.id,
    si.NumUnivoco,
    si.NumIntervento,
    si.DataIngresso,
    si.DataPrevista,
    CASE 
      WHEN IFNULL(si.Nominativo,'') <> '' THEN si.Nominativo
      ELSE CONCAT(u.RagioneSociale, ' ', IFNULL(u.CognomeNome, ''))
    END AS Utente,
    CONCAT(u1.RagioneSociale, ' ', IFNULL(u1.CognomeNome, '')) AS Operatore,
    stb.Descrizione AS Tipobene,
    m.Descrizione AS Marca,
    si.Articolo,
    si.Difetto,
    st.Descrizione AS StatoIntervento,
    sl.data as UltimoAggiornamento,
    st.Colore,
    st.Id AS StatoId
  FROM schedeinterventoin si
    LEFT JOIN utenti u ON si.UtentiId = u.Id
    LEFT JOIN utenti u1 ON si.OperatoriId = u1.Id
    LEFT JOIN schedatipobene stb ON si.TipoBeneId = stb.id
    LEFT JOIN marche m ON si.Marca = m.id
    LEFT JOIN (
        SELECT sklav.*
        FROM schedelavorazione sklav
        INNER JOIN (
            SELECT SchedaId, MAX(DATA) AS MaxData
            FROM schedelavorazione
            GROUP BY SchedaId
        ) sklav2 
        ON sklav.SchedaId = sklav2.SchedaId AND sklav.DATA = sklav2.MaxData
    ) sl ON sl.SchedaId = si.id
    LEFT JOIN statointerventi st ON sl.StatoInterventoId = st.id
    where year(si.dataingresso)=parAnno;
END */$$
