DELIMITER $$

/*!50003 CREATE DEFINER=`ks_taikun`@`%` PROCEDURE `Giacenza`(IN pId INT(11))
BEGIN
    DECLARE conta INT(11);
    DECLARE TeC INT(1);
    SELECT COUNT(*) INTO conta FROM articoli_giacenze WHERE ArticoliId=pId;
    
    IF IFNULL(conta,0)=0 THEN
	SELECT NULL AS id,1 AS MagazziniId,NULL AS ArticoliId,
	0 AS Giacenza,0 AS inordine,0 AS disponibilita,
	0 AS impeganta,0 AS scortaminima, -1 AS TCid;
    ELSE
	SELECT IFNULL(TC,0) INTO TeC FROM Aziende WHERE id=1;
	IF TeC=1 THEN
		SELECT MagazziniId,ArticoliId, 
			SUM(Giacenza) AS Giacenza, 
			SUM(InOrdine)AS InOrdine, 
			SUM(Disponibilita) AS Disponibilita,
			SUM(Impegnata) AS Impegnata, 
			SUM(ScortaMinima) AS Scortaminima
		FROM 
			articoli_giacenze 
		WHERE 
			ArticoliId=pId
		GROUP BY 
			MagazziniId,ArticoliId;
	ELSE
		SELECT * FROM articoli_giacenze WHERE ArticoliId=pId;
	END IF;			
    END IF;
    END */$$
