	SELECT	R.Codice, SUM(R.qnt*tp.segno) AS Giacenza
	FROM documentirighe R
	INNER JOIN documenti AS T ON R.DocumentiId=T.id
	INNER JOIN tipodocumenti AS TP ON T.TipoDocumentiId=TP.id
	WHERE tp.segno <>0 AND R.ArticoliId=parArticolo AND t.DataDocumento between parInizio and parFine
	GROUP BY r.codice;
