/* Procedure structure for procedure `liquidazioni` */

/*!50003 DROP PROCEDURE IF EXISTS  `liquidazioni` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`ks_taikun`@`%` PROCEDURE `liquidazioni`(IN parTipoDocumentiId VARCHAR(100),in parAgenteId int,in parDal date,in parAl date)
BEGIN
	SET @TipoDoc:=parTipoDocumentiId;
	
	select 
		round(documentirighe.importo,2) as Importo,
		round((documentirighe.importo*coalesce(documentiagenti.Provvigione1,0)/100),2) as OnereAgente,
		round(ifnull(documentiagenti.Provvigione1,0),2) as Provvigione1,
		documenti.DataDocumento, documenti.NDocumento,documentipie.TotImponibile,documentipie.TotaleDocumento
		,documenti.bis,tipodocumenti.Descrizione AS TipoDoc
		,CONCAT(`utenti`.`RagioneSociale`,' ',IFNULL(`utenti`.`CognomeNome`,'')) AS `Agente`,
		documentiagenti.Pagato,documentiagenti.DocumentiId,
		concat(clifor.ragionesociale,' ',ifnull(clifor.cognomenome,'')) as Cliente
	from documentirighe
	left join documentiagenti on documentirighe.id = documentiagenti.DocumentiRigheId
	left join documenti on documenti.id=documentirighe.DocumentiId
	left join tipodocumenti on documenti.TipoDocumentiId=tipodocumenti.id
	left join documentipie on documenti.id=documentipie.DocumentiId
	LEFT JOIN `utenti` ON `documentiagenti`.`AgenteId` = `utenti`.`Id`
	LEFT JOIN utenti clifor ON clifor.id=documenti.utentiid 
	where documentiagenti.AgenteId=parAgenteId and 
	FIND_IN_SET(documenti.tipodocumentiid, @TipoDoc) AND
	(documenti.DataDocumento BETWEEN parDal and parAl);
    END */$$
DELIMITER ;

/* Procedure structure for procedure `LiquidazioniCompresso` */

/*!50003 DROP PROCEDURE IF EXISTS  `LiquidazioniCompresso` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`ks_taikun`@`%` PROCEDURE `LiquidazioniCompresso`(
    IN parTipoDocumentiId VARCHAR(100),
    IN parAgenteId INT,
    IN parDal DATE,
    IN parAl DATE
)
BEGIN
    SET @TipoDoc := parTipoDocumentiId;
    SELECT 
        ROUND(SUM(documentirighe.importo),2) AS Importo,
        ROUND(SUM((documentirighe.importo * COALESCE(documentiagenti.Provvigione1,0) / 100)),2) AS OnereAgente,
        documenti.DataDocumento, 
        documenti.NDocumento,
        documentipie.TotImponibile,
        documentipie.TotaleDocumento,
        documenti.bis,
        tipodocumenti.Descrizione AS TipoDoc,
        CONCAT(`utenti`.`RagioneSociale`, ' ', IFNULL(`utenti`.`CognomeNome`, '')) AS `Agente`,
        documentiagenti.Pagato,
        documentiagenti.DocumentiId,
        CONCAT(clifor.ragionesociale, ' ', IFNULL(clifor.cognomenome, '')) AS Cliente
    FROM documentirighe
    LEFT JOIN documentiagenti ON documentirighe.id = documentiagenti.DocumentiRigheId
    LEFT JOIN documenti ON documenti.id = documentirighe.DocumentiId
    LEFT JOIN tipodocumenti ON documenti.TipoDocumentiId = tipodocumenti.id
    LEFT JOIN documentipie ON documenti.id = documentipie.DocumentiId
    LEFT JOIN `utenti` ON documentiagenti.AgenteId = utenti.Id
    LEFT JOIN utenti clifor ON clifor.id = documenti.utentiid 
    WHERE documentiagenti.AgenteId = parAgenteId 
      AND FIND_IN_SET(tipodocumenti.id, @TipoDoc) 
      AND (documenti.DataDocumento BETWEEN parDal AND parAl) 
    GROUP BY 
        documenti.DataDocumento,
        documenti.NDocumento,
        documentiagenti.Pagato,
        documenti.bis,
        tipodocumenti.Descrizione,
        documentipie.TotImponibile,
        documentipie.TotaleDocumento,
        utenti.RagioneSociale,
        utenti.CognomeNome,
        documentiagenti.DocumentiId,
        clifor.ragionesociale,
        clifor.cognomenome
    ORDER BY 
        documenti.DataDocumento DESC;
END */$$
DELIMITER ;

