DELIMITER $$

/*!50003 CREATE DEFINER=`ks_taikun`@`%` PROCEDURE `MacroRaggruppamenti`(IN filtro text)
BEGIN
    SET @filtro1='SELECT
    		settori.id 				AS IdSettore, 
		settori.Descrizione 			AS Settore,
		ifnull(categorie.id,-1) 		AS IdCategoria, 
		ifnull(categorie.Descrizione,'''') 	AS Categoria,
		ifnull(tipologie.id,-1) 		AS idTipologia, 
		ifnull(tipologie.Descrizione,'''') 	AS Tipologia,
		ifnull(gruppi.id,-1)	 		AS idGruppo, 
		ifnull(gruppi.Descrizione,'''') 		AS Gruppo,
		ifnull(sottogruppi.id,-1) 		AS IdSottoGruppo, 
		ifnull(sottogruppi.Descrizione,'''') 	AS SottoGruppo  
	FROM settori
		LEFT JOIN (Select * from categorie where abilitato=1)  		as categorie 	ON settori.id=categorie.`SettoriId`
		LEFT JOIN (Select * from tipologie  where abilitato=1) 		as tipologie 	ON categorie.id=tipologie.`CategorieId`
		LEFT JOIN (Select * from gruppi  where abilitato=1)    		as gruppi 	ON tipologie.id=gruppi.`IdPadre` and gruppi.categorieid=tipologie.categorieid
		LEFT JOIN (Select * from sottogruppi  where abilitato=1) 	as sottogruppi 	ON gruppi.id = sottogruppi.`IdPadre` and sottogruppi.categorieid=gruppi.categorieid
	WHERE settori.id is not null ';
	
    SET @filtro2 = ' ORDER BY settori.Ordinamento, settori.Descrizione, categorie.ordinamento, categorie.Descrizione, tipologie.ordinamento, 
    tipologie.Descrizione, gruppi.ordinamento, gruppi.Descrizione, sottogruppi.ordinamento, sottogruppi.Descrizione';
    SET @filtroHelp = CONCAT(' AND ', filtro);
    IF TRIM(IFNULL(filtro, '')) = '' THEN 
        SET @sSQL = CONCAT(@filtro1, @filtro2);
    ELSE
        SET @sSQL = CONCAT(@filtro1, @filtroHelp, @filtro2);
    END IF;
    PREPARE stmt FROM @sSQL;
    EXECUTE stmt;
    DEALLOCATE PREPARE stmt;
END */$$
DELIMITER ;

