/*!50003 CREATE DEFINER=`ks_taikun`@`%` PROCEDURE `OffertaDaListino`(in pdata date,in pnListino int(11),in pAzienda int(11))
BEGIN
SELECT
	offerte.id
	, offertedettaglio.id as dettaglioid
	, offertedettaglio.MarcheId
	, offertedettaglio.SettoriId
	, offertedettaglio.CategorieId
	, offertedettaglio.TipologieId
	, offertedettaglio.GruppiId
	, offertedettaglio.SottoGruppiId
	, offertedettaglio.ArticoliId
	FROM
	offertedettaglio
	INNER JOIN offerte 
		ON (offertedettaglio.offerteid = offerte.id)
	where (pnlistino between offerte.dalistino and offerte.alistino) and 
	      (pdata BETWEEN offerte.DataInizio and offerte.DataFine) and
	      AziendeId=pAzienda and
	      offerte.abilitato=1;
