	SELECT offerte.id, offerte.Descrizione, offerte.DataInizio, offerte.DataFine, clienti.Id AS idCliente, clienti.RagioneSociale as Cliente, settori.Descrizione AS Settore, categorie.Descrizione AS Categoria, marche.Descrizione AS Marca, tipologie.Descrizione AS Tipologia, offerte.DaListino, offerte.AListino, offerte.QntMinima, offerte.Multipli, offerte.Prezzo, offerte.Sconto, offerte.Abilitato
	FROM ((((((offerte INNER JOIN offerta_dettaglio ON offerte.id = offerta_dettaglio.id) INNER JOIN clienti ON offerta_dettaglio.Cliente = clienti.Id) INNER JOIN articoli ON offerta_dettaglio.Articolo = articoli.id) INNER JOIN settori ON offerta_dettaglio.Settore = settori.id) INNER JOIN categorie ON offerta_dettaglio.Categoria = categorie.id) INNER JOIN marche ON offerta_dettaglio.Marca = marche.id) INNER JOIN tipologie ON offerta_dettaglio.Tipologia = tipologie.id
	WHERE clienti.Id=pIdCli AND offerte.Abilitato=1 AND (pNListino Between Offerte.Dalistino And Offerte.Alistino)
