DELIMITER $$

/*!50003 CREATE DEFINER=`ks_taikun`@`%` PROCEDURE `partitarioconti`(in pDal date,In pAl date,in pConto int(11))
BEGIN
if pConto<>-1 then 
	SELECT
	x.`PnTesteId`          			AS `PnTesteId`,
	x.`Conto`              			AS `Conto`,
	x.`Utente`             			AS `Utente`,
	SUM(x.`Dare`)          			AS `Dare`,
	SUM(x.`Avere`)         			AS `Avere`,
	x.`ContiId`            			AS `ContiId`,
	x.`DataReg`            			AS `DataReg`,
	x.`CausaliContabiliId` 			AS `CausaliContabiliId`,
	x.`SpecificaAssengi`   			AS `SpecificaAssengi`,
	x.`ModalitàPagamento` 			AS `ModalitàPagamento`,
	x.`Documento`          			AS `Documento`,
	x.`DescrTeste`          		AS `DescrTeste`,
	ifnull(pp.pntesteid,0)			as `Collegato`
	FROM 
		(
		    SELECT
			  `pnrighe`.`id`                 AS `id`,
			  `pnrighe`.`PnTesteId`          AS `PnTesteId`,
			  `conti2`.`Descrizione`         AS `Conto`,
			  CONCAT(`utenti`.`RagioneSociale`,' ',IFNULL(`utenti`.`CognomeNome`,''),' - ',`utenti`.`Piva`) AS `Utente`,
			  ifnull(`pnrighe`.`Dare`,0)               AS `Dare`,
			  ifnull(`pnrighe`.`Avere`,0)              AS `Avere`,
			  `pnrighe`.`ContiId`            AS `ContiId`,
			  `pnteste`.`DataReg`            AS `DataReg`,
			  `pnteste`.`CausaliContabiliid` AS `CausaliContabiliId`,
			  `pnrigheextra`.`Descrizione`   AS `SpecificaAssengi`,
			  `pagamentitipo`.`Descrizione`  AS `ModalitàPagamento`,
			  (CASE WHEN (`documenti`.`NProtocollo` <> 0) THEN CONCAT(`tipodocumenti`.`Descrizione`,' N.',`documenti`.`NProtocollo`,' del ',CONVERT(DATE_FORMAT(`documenti`.`DataProtocollo`,'%d/%m/%Y') USING latin1)) ELSE CONCAT(`tipodocumenti`.`Descrizione`,' N.',`documenti`.`NDocumento`,' del ',CONVERT(DATE_FORMAT(`documenti`.`DataDocumento`,'%d/%m/%Y') USING latin1)) END) AS `Documento`,
			  `pnteste`.`Descrizione`        AS `DescrTeste`
			FROM `pnrighe`
				LEFT JOIN `conti2` ON `pnrighe`.`ContiId` = `conti2`.`id`
				LEFT JOIN `scadenziario` ON `pnrighe`.`Scadenzeid` = `scadenziario`.`id`
				LEFT JOIN `pnteste`ON `pnrighe`.`PnTesteId` = `pnteste`.`id`
			        LEFT JOIN `documenti` ON `pnrighe`.`DocumentiId` = `documenti`.`id`
			        LEFT JOIN `utenti` ON `pnteste`.`UtentiId` = `utenti`.`Id`
				LEFT JOIN `tipodocumenti` ON `documenti`.`TipoDocumentiId` = `tipodocumenti`.`id`
				LEFT JOIN `pnrigheextra` ON `pnrighe`.`id` = `pnrigheextra`.`pnrigheid`
				LEFT JOIN `pagamentitipo` ON `pnrigheextra`.`pagamentitipoid` = `pagamentitipo`.`id`
			WHERE pnteste.datareg BETWEEN pDal AND pAl AND `pnrighe`.`ContiId` = pConto 
		) 
	AS X
	left join (select distinct pntesteid from PartitarioPrelevato) as PP on x.pntesteid=pp.pntesteid
	GROUP BY x.`PnTesteId`,
	         x.`Conto`,
	         x.`ContiId`,
	         x.`Documento`,
	         x.`DescrTeste`;
else
	SELECT
	x.`PnTesteId`          			AS `PnTesteId`,
	x.`Conto`              			AS `Conto`,
	x.`Utente`             			AS `Utente`,
	SUM(x.`Dare`)          			AS `Dare`,
	SUM(x.`Avere`)         			AS `Avere`,
	x.`ContiId`            			AS `ContiId`,
	x.`DataReg`            			AS `DataReg`,
	x.`CausaliContabiliId` 			AS `CausaliContabiliId`,
	x.`SpecificaAssengi`   			AS `SpecificaAssengi`,
	x.`ModalitàPagamento` 			AS `ModalitàPagamento`,
	x.`Documento`          			AS `Documento`,
	x.`DescrTeste`          		AS `DescrTeste`,
	IFNULL(pp.pntesteid,0)			AS `Collegato`
	FROM 
		(
		    SELECT
			  `pnrighe`.`id`                 AS `id`,
			  `pnrighe`.`PnTesteId`          AS `PnTesteId`,
			  `conti2`.`Descrizione`         AS `Conto`,
			  CONCAT(`utenti`.`RagioneSociale`,' ',IFNULL(`utenti`.`CognomeNome`,''),' - ',`utenti`.`Piva`) AS `Utente`,
			  ifnull(`pnrighe`.`Dare`,0)               AS `Dare`,
			  ifnull(`pnrighe`.`Avere`,0)              AS `Avere`,
			  `pnrighe`.`ContiId`            AS `ContiId`,
			  `pnteste`.`DataReg`            AS `DataReg`,
			  `pnteste`.`CausaliContabiliid` AS `CausaliContabiliId`,
			  `pnrigheextra`.`Descrizione`   AS `SpecificaAssengi`,
			  `pagamentitipo`.`Descrizione`  AS `ModalitàPagamento`,
			  (CASE WHEN (`documenti`.`NProtocollo` <> 0) THEN CONCAT(`tipodocumenti`.`Descrizione`,' N.',`documenti`.`NProtocollo`,' del ',CONVERT(DATE_FORMAT(`documenti`.`DataProtocollo`,'%d/%m/%Y') USING latin1)) ELSE CONCAT(`tipodocumenti`.`Descrizione`,' N.',`documenti`.`NDocumento`,' del ',CONVERT(DATE_FORMAT(`documenti`.`DataDocumento`,'%d/%m/%Y') USING latin1)) END) AS `Documento`,
			  `pnteste`.`Descrizione`        AS `DescrTeste`
			FROM `pnrighe`
				LEFT JOIN `conti2` ON `pnrighe`.`ContiId` = `conti2`.`id`
				LEFT JOIN `scadenziario` ON `pnrighe`.`Scadenzeid` = `scadenziario`.`id`
				LEFT JOIN `pnteste`ON `pnrighe`.`PnTesteId` = `pnteste`.`id`
			        LEFT JOIN `documenti` ON `pnrighe`.`DocumentiId` = `documenti`.`id`
			        LEFT JOIN `utenti` ON `pnteste`.`UtentiId` = `utenti`.`Id`
				LEFT JOIN `tipodocumenti` ON `documenti`.`TipoDocumentiId` = `tipodocumenti`.`id`
				LEFT JOIN `pnrigheextra` ON `pnrighe`.`id` = `pnrigheextra`.`pnrigheid`
				LEFT JOIN `pagamentitipo` ON `pnrigheextra`.`pagamentitipoid` = `pagamentitipo`.`id`
			WHERE pnteste.datareg BETWEEN pDal AND pAl
		) 
	AS X
	LEFT JOIN (SELECT DISTINCT pntesteid FROM PartitarioPrelevato) AS PP ON x.pntesteid=pp.pntesteid
	GROUP BY x.`PnTesteId`,x.`Conto`,x.`Utente`,x.`ContiId`,x.`DataReg`,x.`CausaliContabiliId`,x.`Documento`,x.`DescrTeste`;
end if;
END */$$
