/*!50003 CREATE DEFINER=`ks_taikun`@`%` PROCEDURE `RiepilogoSchedaIntervento`(in parSchedaId int(11))
BEGIN
	CREATE TEMPORARY TABLE IF NOT EXISTS tmp_riepilogo (
		SchedaId INT,
		Descrizione TEXT
	);
	
	/* Inserimento Testata Intervento in tabella temporanea */
	Insert into tmp_riepilogo(SchedaId,Descrizione)
	Select parSchedaId,concat('Accettazione avvenuta il giorno :', DATE_FORMAT(DataIngresso, '%d/%m/%Y'),CHAR(13),
				  'Nominativo :',Nominativo,CHAR(13),
				  'Difetto dichiarato :',difetto,CHAR(13),
				  'Stato Usura :',statousura,CHAR(13),
				  'Note :',infonote,char(13)) 
	FROM schedeinterventoin WHERE id=parSchedaId;
	
	/* Inserimento Fasi di lavorazione in tabella temporanea */
	INSERT INTO tmp_riepilogo(SchedaId,Descrizione) values (parSchedaId,Concat('Dettaglio lavorazione eseguita :',char(13)));
	
	INSERT INTO tmp_riepilogo(SchedaId,Descrizione)
	SELECT parSchedaId,CONCAT('Il giorno :',DATE_FORMAT(`data`, '%d/%m/%Y'),' : ',Motivazione,CHAR(13))
	FROM schedelavorazione WHERE schedaId=parSchedaId and ifnull(motivazione,'')<>'';
	-- Risultati finali
	SELECT * FROM tmp_riepilogo;
	
	-- Rimozione esplicita della tabella temporanea
	DROP TEMPORARY TABLE IF EXISTS tmp_riepilogo;
	
END */$$
