/* Procedure structure for procedure `RipristinaGiacenza` */

/*!50003 DROP PROCEDURE IF EXISTS  `RipristinaGiacenza` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`ks_taikun`@`%` PROCEDURE `RipristinaGiacenza`(in pDocumentiId int(11))
BEGIN
	DECLARE finito INT default 0;
	
	
	
	DECLARE pArticolo int(11);
	DECLARE pQnt double(15,3);
	DECLARE pMovim int(11);
	
	DECLARE dati CURSOR FOR
	SELECT articoliId,qnt,movimento 
	FROM documentirighe
        WHERE DocumentiId = pDocumentiId;
	
        DECLARE CONTINUE HANDLER FOR SQLSTATE '02000'
                SET finito = 1;
	
	OPEN dati;
	
	FETCH dati INTO pArticolo,pQnt,pMovim;
	
	
	ciclo: WHILE NOT finito DO
		if pMovim<>0 then 
			update articoli_giacenze set giacenza=giacenza+(-1*pMovim*pQnt) where ArticoliId=pArticolo;
		end if;
		
		FETCH dati INTO pArticolo,pQnt,pMovim;              
        END WHILE ciclo;
			
    END */$$
