	LEFT JOIN causalitrasporto ON (documentipie.CausaliTrasportoId = causalitrasporto.id)
	LEFT JOIN vettori ON (documentipie.VettoriId = vettori.id)
	LEFT JOIN Articoli ON (Articoli.id = documentirighe.Articoliid)
