	LEFT JOIN utenticontabili ON (utenti.id=utenticontabili.UtenteId) AND utenticontabili.`Predefinito`=1
	LEFT JOIN documenticollegati dc ON dc.documentiid=documenti.id
	LEFT JOIN vtagliecolori TC ON documentirighe.tcid=tc.id
