DELIMITER $$

/*!50003 CREATE DEFINER=`ks_taikun`@`%` PROCEDURE `TrovaValoreSospetto`(IN search_text VARCHAR(1000))
BEGIN
  DECLARE done INT DEFAULT FALSE;
  DECLARE t_name VARCHAR(64);
  DECLARE c_name VARCHAR(64);
  DECLARE dyn_sql TEXT;
  DECLARE cur CURSOR FOR
    SELECT table_name, column_name
    FROM information_schema.columns
    WHERE table_schema = DATABASE()
      AND data_type IN ('varchar', 'char', 'text', 'mediumtext', 'longtext');
  DECLARE CONTINUE HANDLER FOR NOT FOUND SET done = TRUE;
  OPEN cur;
  read_loop: LOOP
    FETCH cur INTO t_name, c_name;
    IF done THEN
      LEAVE read_loop;
    END IF;
    SET @dyn_sql = CONCAT(
      'SELECT ''', t_name, ''' AS TableName, ''', c_name, ''' AS ColumnName, `',
      c_name, '` AS ValueFound FROM `', DATABASE(), '`.`', t_name,
      '` WHERE `', c_name, '` LIKE "%', search_text, '%"'
    );
    PREPARE stmt FROM @dyn_sql;
    EXECUTE stmt;
    DEALLOCATE PREPARE stmt;
  END LOOP;
  CLOSE cur;
END */$$
DELIMITER ;

