DELIMITER $$

/*!50003 CREATE DEFINER=`ks_taikun`@`%` PROCEDURE `VettoreCostoXpeso`(in pIdVettore int(11), in pPeso DOUBLE(15,3))
BEGIN
DECLARE Tot int(11);
Declare pesom double(15,3);
	Select Count(*) into Tot from (
		SELECT
		min(vettoricosti.CostoFisso) AS Spedizione
		, vettori.AssicurazionePercentuale
		, vettori.AssicurazioneMinimo
		, vettori.ContrassegnoPercentuale
		, vettori.ContrassegnoFisso
		, vettori.ContrassegnoMinimo
		FROM
		vettori
		INNER JOIN vettoricosti 
			ON (vettori.id = vettoricosti.VettoriId)
		WHERE (vettoricosti.VettoriId =pIdVettore AND vettoricosti.PesoMax >=pPeso)
		GROUP BY vettori.AssicurazionePercentuale, vettori.AssicurazioneMinimo, 
			vettori.ContrassegnoPercentuale, vettori.ContrassegnoFisso, 
			vettori.ContrassegnoMinimo) t;
	if ifnull(tot,0)=0 then 
		set pesom=0;
		SELECT MAX(pesomax) into pesom FROM vettoricosti WHERE vettoriid=pIdVettore;
		SELECT
		MIN(vettoricosti.CostoFisso) AS Spedizione
		, vettori.AssicurazionePercentuale
		, vettori.AssicurazioneMinimo
		, vettori.ContrassegnoPercentuale
		, vettori.ContrassegnoFisso
		, vettori.ContrassegnoMinimo
		FROM
		vettori
		INNER JOIN vettoricosti 
			ON (vettori.id = vettoricosti.VettoriId)
		WHERE (vettoricosti.VettoriId =pIdVettore AND vettoricosti.PesoMax >=pesom)
		GROUP BY vettori.AssicurazionePercentuale, vettori.AssicurazioneMinimo, 
			vettori.ContrassegnoPercentuale, vettori.ContrassegnoFisso, 
			vettori.ContrassegnoMinimo;
	else
		SELECT
		MIN(vettoricosti.CostoFisso) AS Spedizione
		, vettori.AssicurazionePercentuale
		, vettori.AssicurazioneMinimo
		, vettori.ContrassegnoPercentuale
		, vettori.ContrassegnoFisso
		, vettori.ContrassegnoMinimo
		FROM vettori
		INNER JOIN vettoricosti 
			ON (vettori.id = vettoricosti.VettoriId)
		WHERE (vettoricosti.VettoriId =pIdVettore AND vettoricosti.PesoMax >=pPeso)
		GROUP BY vettori.AssicurazionePercentuale, vettori.AssicurazioneMinimo, 
			vettori.ContrassegnoPercentuale, vettori.ContrassegnoFisso, 
			vettori.ContrassegnoMinimo;
	end if;
    END */$$
DELIMITER ;

/* Procedure structure for procedure `VettoreCostoXpesoPromo` */

/*!50003 DROP PROCEDURE IF EXISTS  `VettoreCostoXpesoPromo` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`ks_taikun`@`%` PROCEDURE `VettoreCostoXpesoPromo`(IN pIdVettore INT(11), IN pListino INT(11),IN pData DATE,pPeso DOUBLE(15,5),IN pImponibile DOUBLE (15,5))
BEGIN
	select
	vettoricosti.CostoFisso AS Spedizione
	, vettoricosti.Costo_Percentuale as Percentuale
	, vettori.AssicurazionePercentuale
	, vettori.AssicurazioneMinimo
	, vettori.ContrassegnoPercentuale
	, vettori.ContrassegnoFisso
	, vettori.ContrassegnoMinimo
	FROM vettori
	LEFT JOIN vettoricosti ON vettori.id=vettoricosti.vettoriid
	WHERE vettori.promo=1 
	AND vettori.Listini_Abilitati LIKE CONCAT('%',pListino,';%')
	AND vettoricosti.PesoMax>=pPeso AND Soglia_Minima<=pImponibile	
	AND pData BETWEEN vettori.Promo_Data_Inizio AND vettori.Promo_Data_Fine;
	
  END */$$
