/* Procedure structure for procedure `View_Promo` */

/*!50003 DROP PROCEDURE IF EXISTS  `View_Promo` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`ks_taikun`@`%` PROCEDURE `View_Promo`(IN pAzienda INT(11), IN pArticolo INT(11), IN pListino INT(11))
BEGIN
	SELECT * From voffertearticoli as va
	join articoli_listini AS l On ArticoliId=pArticolo
	WHERE (l.NListino=pListino) AND (va.id=pArticolo) AND (pListino BETWEEN `OfferteDaListino` AND `OfferteAListino`)
	group by va.OfferteID	
	ORDER BY va.id;
    END */$$
