/*!50003 CREATE DEFINER=`ks_taikun`@`%` PROCEDURE `View_Wishlist_NoPromo`(IN pAzienda INT(11), IN pUtente INT(11), IN pListino INT(11))
BEGIN
	SELECT A.id,A.codice,A.Ean, A.descrizione1,A.descrizione2,A.`DescrizioneLunga`,A.img1,l.nlistino,A.iva,A.Peso,A.ListinoUfficiale,
	A.Mesi,A.Garanzia, PrezzoIvato, l.prezzo,l.prezzoivato,g.`Giacenza`,g.`Impegnata`
	FROM articoli AS A
	LEFT JOIN articoli_listini AS L ON l.`ArticoliId`=A.`id`
