/* Procedure structure for procedure `xVetrina` */

/*!50003 DROP PROCEDURE IF EXISTS  `xVetrina` */;

DELIMITER $$

/*!50003 CREATE DEFINER=`ks_taikun`@`%` PROCEDURE `xVetrina`(in pAzienda int(11), in pListino int(11), IN pLimit int(5))
BEGIN
	SELECT A.id,A.codice, A.descrizione1,A.descrizione2,A.`DescrizioneLunga`,A.img1,l.nlistino,A.iva,
	OD.offerteid,
	CASE 
		WHEN IFNULL(o.prezzo,0)=0 AND IFNULL(o.sconto,0)=0 THEN l.prezzo
		WHEN IFNULL(o.prezzo,0)>0 THEN o.prezzo
		WHEN IFNULL(o.prezzo,0)=0 AND IFNULL(o.sconto,0)>0 THEN l.prezzo-(l.prezzo*o.sconto/100)
	END Prezzo, 
	
	CASE 
		WHEN IFNULL(o.prezzo,0)=0 AND IFNULL(o.sconto,0)=0 THEN l.prezzo+(l.prezzo*a.iva/100)
		WHEN IFNULL(o.prezzo,0)>0 THEN o.prezzo+(o.prezzo*a.iva/100)
		WHEN IFNULL(o.prezzo,0)=0 AND IFNULL(o.sconto,0)>0 THEN (l.prezzo-(l.prezzo*o.sconto/100))+(l.prezzo-(l.prezzo*o.sconto/100)*a.iva/100)
	END PrezzoIvato, l.prezzo,l.prezzoivato, g.`Giacenza`,IF(ISNULL(O.id),0,1) AS `InOfferta`
	FROM articoli AS A
	LEFT JOIN `offertedettaglio` AS OD ON OD.`ArticoliId`=A.`id`
	LEFT JOIN offerte AS O ON O.id=OD.`OfferteId`
	LEFT JOIN articoli_listini AS L ON l.`ArticoliId`=A.`id`
	LEFT JOIN articoli_giacenze AS G ON G.`ArticoliId`=A.`id`
	WHERE l.`NListino`= pListino AND A.`Vetrina`=1 AND o.aziendeid=pAzienda AND 
	l.nlistino BETWEEN o.dalistino AND o.alistino
	order by RAND() limit pLimit;
    END */$$
DELIMITER ;

